import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import 'bulma/css/bulma.css';
import Bikes from './components/Bikes';
import Pagination from './components/Pagination';
import Filter from './components/Filter';
import Header from './components/Header';

const App = () => {

  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [bikesPerPage] = useState(10);
  const [allBikes, setAllBikes] = useState('')

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const res = await axios.get(`https://bikewise.org:443/api/v2/incidents?`)
      setBikes(res.data.incidents);
      setLoading(false);
      console.log(res)
    }

    fetchData();


  }, [] );

  const date = new Date().getTime() / 1000

  // Get current Bikes
  const indexLastPage = currentPage * bikesPerPage;
  const indexFirtsPage = indexLastPage - bikesPerPage;
  const currentBikes = bikes.slice(indexFirtsPage, indexLastPage);

  // Change Page
  const paginate = (pageNumber) => setCurrentPage(pageNumber)

  const onSearchText = (textValue) => {
    const lower = textValue.toLowerCase()
    console.log(lower)
    const copiedCurrentBikes = [...currentBikes]
    const filteredBikes = copiedCurrentBikes.filter(value => value.title.includes(lower))
    setBikes(filteredBikes)
  }

  return (
    <div className="App">
      <Header />
      <Filter handlerInput={onSearchText}/>
      <Bikes bikes={currentBikes} loading={loading}/>
      <Pagination bikesPerPage={bikesPerPage} totalBikes={bikes.length} paginate={paginate}/>
    </div>
  );
}

export default App;
