import React from 'react'
import Logo from './../assets/bike.png';

const Bikes= ({bikes, loading}) => {

    if(loading){
        return <h2>Loading...</h2>
    }

    return (
        <div>
            <ul>
                {
                    bikes.map(bike => (

                        <div key={bike.id} className="box">
                            <article className="media">
                                <div className="media-left">
                                    <figure className="image">
                                        <img src={bike.media.image_url === null ? Logo : bike.media.image_url} alt={bike.id}/>
                                    </figure>
                                    <div> <strong> Location </strong> <br/> <span> {bike.address} </span> </div>
                                </div>
                                <div className="media-content">
                                    <div className="content">
                                        <h1> {bike.title} </h1>
                                        <br/>
                                        <p>
                                            {bike.description === null || bike.description === '' ? 'No description added on this report' : bike.description}
                                        </p>
                                        <div className="content-date">
                                            <p> <strong>Date of theft</strong> <br/> {bike.occurred_at} </p>
                                            <p> <strong>Reported Date</strong> <br/> {bike.updated_at} </p>
                                        </div>
                                    </div>
                                </div>
                            </article>
                        </div>



                        // <li key={bike.id}>
                        //     {bike.title}
                        // </li>
                    ))
                }
            </ul>
        </div>
    )
}

export default Bikes
