import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import 'bulma/css/bulma.css';
import Bikes from './components/Bikes';
import Pagination from './components/Pagination';
import Filter from './components/Filter';
import Header from './components/Header';

const App = () => {

  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [bikesPerPage] = useState(10);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const res = await axios.get('https://bikewise.org:443/api/v2/incidents?page=1&proximity_square=100')
      setBikes(res.data.incidents);
      setLoading(false);
      console.log(res)
    }


    // fetchData();

  }, [] );


  // Get current Bikes
  const indexLastPage = currentPage * bikesPerPage;
  const indexFirtsPage = indexLastPage - bikesPerPage;
  const currentBikes = bikes.slice(indexFirtsPage, indexLastPage);

  // Change Page
  const paginate = (pageNumber) => setCurrentPage(pageNumber)

  return (
    <div className="App">
      <div>
        <button onClick={this.fetchData()}> Search</button>
      </div>
      <Header />
      <Filter totalBikes={bikes.length} />
      <Bikes bikes={currentBikes} loading={loading}/>
      <Pagination bikesPerPage={bikesPerPage} totalBikes={bikes.length} paginate={paginate}/>
    </div>
  );
}

export default App;
